mapper.select:
SELECT ct.template_path
FROM cms_category_template ct
WHERE ct.category_id = #{categoryId}

SELECT COUNT(*)
from cms_category
where parent_id = #{parentId}

SELECT own.id,own.`name`,own.code,own.path AS path,own.cover,own.path_rule AS pathRule, own.only_url AS onlyUrl ,own.contain_child AS containChild,own.single_page AS singlePage,own.page_size AS pageSize, own.allow_contribute AS allowContribute,own.top_pages AS topPages,own.remote_url AS remoteUrl, attr.title,attr.keywords,attr.description,attr.`data`, own.sort,own.hidden,own.category_model_id AS categoryModelId,own.create_time as createTime ,own.update_time AS updateTime, IFNULL(own.parent_id,'0') AS parentId, IFNULL(parent.name,'父分类') AS parentName FROM cms_category own LEFT JOIN cms_category parent ON parent.id=own.parent_id LEFT JOIN cms_category_attribute attr ON attr.category_id = own.id
own.id = #{id}
AND own.code = #{code}
AND own.site_id = #{siteId}

SELECT COUNT(1) FROM cms_content content LEFT JOIN cms_category category ON content.category_id = category.id


        AND content.category_id = #{categoryId}


        AND category.code =#{code}

    AND `status` =1

SELECT own.id,own.`name`,own.code,own.path AS path,own.cover,own.path_rule AS pathRule, own.only_url AS onlyUrl ,own.contain_child AS containChild,own.single_page AS singlePage,own.page_size AS pageSize, own.allow_contribute AS allowContribute,own.top_pages AS topPages,own.remote_url AS remoteUrl, attr.title,attr.keywords,attr.description,attr.`data`, own.sort,own.hidden,own.category_model_id AS categoryModelId,own.create_time as createTime ,own.update_time AS updateTime, IFNULL(own.parent_id,'0') AS parentId, IFNULL(parent.name,'父分类') AS parentName FROM cms_category own LEFT JOIN cms_category parent ON parent.id=own.parent_id LEFT JOIN cms_category_attribute attr ON attr.category_id = own.id
own.parent_id = #{id}
order by own.sort

SELECT own.id,own.`name`, own.parent_id parentId, own.code, own.path, own.cover, own.only_url onlyUrl, own.contain_child containChild, own.page_size pageSize, own.allow_contribute allowContribute, own.sort, own.hidden, own.category_model_id categoryModelId, own.create_time createTime , own.update_time updateTime,IFNULL(parent.name,'父分类') as parentName FROM cms_category own LEFT JOIN cms_category parent ON parent.id=own.parent_id
own.parent_id = #{parentId}
ORDER BY own.sort ASC

mapper.update:
update cms_category
name = #{name}, parent_id = #{parentId}, site_id = #{siteId}, code = #{code}, cover = #{cover}, path_rule = #{pathRule}, path = #{path}, only_url = #{onlyUrl}, remote_url = #{remoteUrl}, page_size = #{pageSize}, allow_contribute = #{allowContribute}, sort = #{sort}, hidden = #{hidden}, category_model_id = #{categoryModelId}, single_page = #{singlePage}, top_pages = #{topPages}, contain_child = #{containChild}, create_id = #{createId}, modified_id = #{modifiedId}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}
